import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TeamMemberPage = () => {
    const [teamMembers, setTeamMembers] = useState([]);
    const [teamGames, setTeamGames] = useState([]);
    const [teamInfo, setTeamInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null); // Add error state variable
    let { teamID } = useParams();

    useEffect(() => {
        const getAllTeamMembers = async () => {
            try {
                const response = await fetch('http://localhost:9000/getAllTeamMembers', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ teamID: teamID }),
                    credentials: 'include',
                });
                if (!response.ok) throw new Error('Error fetching team members');
                const data = await response.json();
                setTeamMembers(data);
            } catch (error) {
                setError(error.message);
            }
        };

        const getTeamGames = async () => {
            try {
                const response = await fetch('http://localhost:9000/getTeamGames', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ teamID: teamID }),
                    credentials: 'include',
                });
                if (!response.ok) throw new Error('Error fetching team games');
                const data = await response.json();
                setTeamGames(data);
            } catch (error) {
                setError(error.message);
            }
        };

        const getTeamInfo = async () => {
            try {
                const response = await fetch('http://localhost:9000/getTeamInfo', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ teamID: teamID }),
                    credentials: 'include',
                });
                if (!response.ok) throw new Error('Error fetching team info');
                const data = await response.json();
                setTeamInfo(data);
            } catch (error) {
                setError(error.message);
            }
        };

        Promise.all([getAllTeamMembers(), getTeamGames(), getTeamInfo()])
            .then(() => setLoading(false))
            .catch((error) => setError(error.message));
    }, [teamID]);

    if (loading) {
        return <div>
            <h1 style={{marginTop:"15%",textAlign:"center",fontSize:"4vw",paddingBottom:"30%"}}>
                Loading....
            </h1>
        </div>
    }

    if (error) {
        return <div>
            <h1 style={{marginTop:"15%",textAlign:"center",fontSize:"4vw",paddingBottom:"30%"}}>
                Error: {error}
            </h1>
        </div>
    }

    return (
        <div>
            <h1 style={{textAlign:"center"}}>{teamInfo ? teamInfo[0].Name : ""}</h1>
            <h2 style={{textAlign:"center"}}> VALUE: {teamInfo ? teamInfo[0].Team_Value : ""}</h2>
        </div>
    );
};

export default TeamMemberPage;
