import React, {useState, useEffect} from 'react';
import EventBoxInfo from './EventBox';

const FindEventsPage = () =>{
    const [events, setEvents] = useState([]); // State to store events
    const [searchTerm, setSearchTerm] = useState(''); // State to store search term

    useEffect(() => {
        getAllEvents();
    }, []); // Empty dependency array means this effect runs once on component mount.

    const getAllEvents = async()=>{
        try {
            const response = await fetch('http://localhost:9000/getAllEvents', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include'
            });
            if (response.ok) {
                const data = await response.json();
                setEvents(data); // Store events data
            } else {
                throw new Error('An unexpected error occurred');
            }
        } catch (error) {
            console.error(error);
        }
    };

    // Filter events based on search term
    const filteredEvents = events.filter(event =>
        event.Name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return(
        <div>
            <input
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div style={{ display: "flex", flexWrap: "wrap", marginTop: "10%" }}>
                {filteredEvents.map((event, index) => (
                    <div style={{ flex: "1 0 21%", margin: "1%", listStyleType:"none" }} key={index}>
                        <EventBoxInfo
                            competitionName={event.Name}
                            startDate={event.StartDate}
                            location={event.Location}
                            type={event.GameType}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default FindEventsPage;
