import React, { useState } from 'react';
import './stylesFiles/createAccount.css';

const CreateAccount = () => {
    const [hasContent, setHasContent] = useState(false);

    const handleBlur = (e) => {
        if (e.target.value !== "") {
            setHasContent(true);
        } else {
            setHasContent(false);
        }
    }

    return (
        <div style={{backgroundImage:"linear-gradient(to bottom right,black,darkblue)",paddingTop:"5%", border:"solid 2px red"}}>
            <h1 style={{fontSize:"4vw"}}>Create Account</h1>
            <form style={{marginTop:"5%",backgroundImage:"linear-gradient(to bottom right,black,darkblue)"}}>
                <div className="col-3 input-effect">
                    <input
                        className={`effect-17 ${hasContent ? 'has-content' : ''}`}
                        type="email"
                        placeholder=""
                        onBlur={handleBlur}
                    />
                    <label>Email Address</label>
                    <span className="focus-border"></span>
                </div>
                <div className="col-3 input-effect">
                    <input
                        className={`effect-17 ${hasContent ? 'has-content' : ''}`}
                        type="text"
                        placeholder=""
                        onBlur={handleBlur}
                    />
                    <label>Last Name</label>
                    <span className="focus-border"></span>
                </div>
                <div className="col-3 input-effect">
                    <input
                        className={`effect-17 ${hasContent ? 'has-content' : ''}`}
                        type="text"
                        placeholder=""
                        onBlur={handleBlur}
                    />
                    <label>First Name</label>
                    <span className="focus-border"></span>
                </div>
            </form>
        </div>
    );
}

export default CreateAccount;
