import React, { useState,useEffect } from 'react';
import styles from './stylesFiles/createAccount.css';



const InputTextField= (props) => {

    const [hasContent, setHasContent] = useState(false);

    const handleBlur = (e) => {
        if (e.target.value !== "") {
            setHasContent(true);
        } else {
            setHasContent(false);
        }
    }

    useEffect(() => {
        if(props.value !== ""){
            setHasContent(true);
        }else{
            setHasContent(false);
        }
    }, [props.value]);

    return (
        <div>
            <input placeholder=""  type={props.type} className={`textField ${hasContent ? 'has-content' : ''}`}>
                <label>{props.label}</label>
                <span className="focus-border"></span>
            </input>
        </div>
    );
}

export default InputTextField;
