import React, {useEffect, useState} from 'react';
import './stylesFiles/createAccount.css';
import InputTextField from './inputTextField';
import LinkButton from "./linkButton";

const CreateAccount = () => {

    const [hasContent, setHasContent] = useState(false);

    const handleBlur = (e) => {
        if (e.target.value !== "") {
            setHasContent(true);
        } else {
            setHasContent(false);
        }
    }


    return (
        <div style={{backgroundImage:"linear-gradient(to top left,black,darkblue)",paddingTop:"5%"}}>
            <h1 style={{fontSize:"4vw"}}>Create Account</h1>
            <form>
                <ul className="createAccountList" style={{listStyleType:"none", marginTop:"5%",paddingRight:"30%", paddingLeft: "30%",paddingBottom:"5%"}}>
                    <li style={{position: "relative"}}>
                        <InputTextField type="text" label="First Name" className={`textField ${hasContent ? 'has-content' : ''}`}></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="text" label="Last Name" className={`textField ${hasContent ? 'has-content' : ''}`}></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="email" label="Email Address" className={`textField ${hasContent ? 'has-content' : ''}`}></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="password" label="Create strong password" className={`textField ${hasContent ? 'has-content' : ''}`}></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="pasword" label="Retype password" className={`textField ${hasContent ? 'has-content' : ''}`}></InputTextField>
                    </li>
                    <li style={{marginTop:"10%"}}>
                        <label style={{fontSize:"25px", paddingRight:"5%"}} htmlFor="position_selector">Which position do you play:</label>
                        <select style={{paddingRight: "10px", paddingLeft:"10px",paddingTop:"5px",paddingBottom:"5px",background:"#edf2ff",border:"none", borderRadius:"20px"}} name="position" id="position_selector">
                            <option value="ATT">Attacker</option>
                            <option value="MID">Midfielder</option>
                            <option value="DEF">Defender</option>
                            <option value="G">Goalkeeper</option>
                        </select>
                    </li>
                    <li style={{marginTop:"10%"}}>
                        <LinkButton link="/createAccount" buttonText="Start your journey"></LinkButton>
                    </li>
                </ul>
            </form>
        </div>
    );
}

export default CreateAccount;
