import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TeamMemberPage = () => {
    const [teamMembers, setTeamMembers] = useState([]);
    const [teamGames, setTeamGames] = useState([]);
    const [teamInfo, setTeamInfo] = useState(null);
    let { teamID } = useParams();

    useEffect(() => {
        const getAllTeamMembers = async () => {
            try {
                const response = await fetch('http://localhost:9000/getAllTeamMembers', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ teamID: teamID }),
                    credentials: 'include',
                });
                if (response.ok) {
                    const data = await response.json();
                    setTeamMembers(data);
                } else {
                    throw new Error('An unexpected error occurred');
                }
            } catch (error) {
                console.error(error);
            }
        };

        const getTeamGames = async () => {
            try {
                const response = await fetch('http://localhost:9000/getTeamGames', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ teamID: teamID }),
                    credentials: 'include',
                });
                if (response.ok) {
                    const data = await response.json();
                    setTeamGames(data);
                } else {
                    throw new Error('An unexpected error occurred');
                }
            } catch (error) {
                console.error(error);
            }
        };

        const getTeamInfo = async () => {
            try {
                const response = await fetch('http://localhost:9000/getTeamInfo', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ teamID: teamID }),
                    credentials: 'include',
                });
                if (response.ok) {
                    const data = await response.json();
                    setTeamInfo(data);
                } else {
                    throw new Error('An unexpected error occurred');
                }
            } catch (error) {
                console.error(error);
            }
        };

        getAllTeamMembers();
        getTeamGames();
        getTeamInfo();
    }, [teamID]);


    return (
        <div>
            <h1 style={{textAlign:"center"}}>{teamInfo.Name}</h1>
            <h2 style={{textAlign:"center"}}>{teamInfo.Team_Value}</h2>

        </div>
    );
};

export default TeamMemberPage;
