import React, {useState} from 'react';
import './stylesFiles/createAccount.css';
import InputTextField from './inputTextField';

const CreateAccount = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        retypePassword: '',
        position: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (formData.password !== formData.retypePassword) {
            alert('Passwords do not match!');
            return;
        }

        // Send a POST request to your Express.js server
        const response = await fetch('http://localhost:9000/create_account', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
        });

        if (response.ok) {
            alert('Account created successfully!');
        } else {
            alert('An error occurred while creating the account.');
        }
    }


    return (
        <div style={{backgroundImage:"linear-gradient(to top left,black,darkblue)",paddingTop:"5%"}}>
            <h1 style={{fontSize:"4vw"}}>Create Account</h1>
            <form onSubmit={handleSubmit}>
                <ul className="createAccountList" style={{listStyleType:"none", marginTop:"5%",paddingRight:"30%", paddingLeft: "30%",paddingBottom:"5%"}}>
                    <li style={{position: "relative"}}>
                        <InputTextField name="firstName" onChange={handleChange} type="text" label="First Name"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField name="lastName" onChange={handleChange} type="text" label="Last Name"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField name="email" onChange={handleChange} type="email" label="Email Address"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField name="password" onChange={handleChange} type="password" label="Create strong password"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField name="retypePassword" onChange={handleChange} type="password" label="Retype password"></InputTextField>
                    </li>
                    <li style={{marginTop:"10%"}}>
                        <label style={{fontSize:"25px", paddingRight:"5%"}} htmlFor="position_selector">Which position do you want to play:</label>
                        <select style={{paddingRight: "10px", paddingLeft:"10px",paddingTop:"5px",paddingBottom:"5px",background:"#edf2ff",border:"none", borderRadius:"20px"}} name="position" id="position_selector" onChange={handleChange}>
                            <option value="ATT">Attacker</option>
                            <option value="MID">Midfielder</option>
                            <option value="DEF">Defender</option>
                            <option value="G">Goalkeeper</option>
                        </select>
                    </li>
                    <li style={{marginTop:"10%"}}>
                        <input className="submit_button" type="submit" value="Start your journey"/>
                    </li>
                </ul>
            </form>
        </div>
    );
}

export default CreateAccount;
