import React, {useState} from 'react';
import InputTextField from './inputTextField';

const LoginForm = () => {
    const [formData, setFormData] = useState({
        userName:'',
        password: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    }
    const handleSubmit = async (event) => {
        event.preventDefault();

        // Verify all fields have data
        for (let property in formData) {
            if (formData[property] === '') {
                alert('Please fill all the fields.');
                return;
            }
        }

        // Send a POST request to your Express.js server
        const response = await fetch('http://localhost:9000/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
        });

        if (response.ok) {
            alert('Login successful!');
        } else {
            alert('An error occurred while login. Please retry');
        }
    }

    return (
        <div>
            <div style={{background: "rgba( 255, 255, 255, 0.1 )",}}>
                <h1 style={{textAlign:"center", marginTop:"5%"}}>Login Here</h1>
                <form>
                    <ul style={{listStyleType:"none",paddingLeft:"25%",paddingRight:"25%",paddingBottom:"5%"}}>
                        <li style={{position: "relative", marginTop:"5%"}}>
                            <InputTextField name="username" onChange={handleChange} type="text" label="Username"></InputTextField>
                        </li>
                        <li style={{position: "relative", marginTop:"5%"}}>
                            <InputTextField name="password" onChange={handleChange} type="password" label="Password"></InputTextField>
                        </li>
                        <li style={{marginTop:"5%"}}>
                            <input className="submit_button" type="submit" value="Login"/>
                        </li>
                    </ul>
                </form>
            </div>
        </div>
    );
}

export default LoginForm;
