import React, {useState,useEffect} from 'react';
import UserBoxInfo from './UserBoxInfo.js';
import GameBoxInfo from "./GameBoxInfo";
import {Link, useNavigate} from 'react-router-dom';
import { useUserSession } from './UserSession';


const UserProfile= () =>{
    const { userSession } = useUserSession();
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [data, setData] = useState(null); // State to store user data
    const [teams,setTeams]=useState([]);
    const [games,setGames]=useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const verifyUserSession = async () => {
            try {
                const response = await fetch('http://localhost:9000/verify-session', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    credentials: 'include'
                });

                if (response.ok) {
                    const data = await response.json();
                    setData(data); // Store user data
                    setIsLoading(false);
                } else if (response.status === 401) {
                    throw new Error('Session verification failed');
                } else {
                    throw new Error('An unexpected error occurred');
                }
            } catch (error) {
                console.error(error);
                if (error.message === 'Session verification failed') {
                    navigate('/login');
                } else {
                    setError(error.message);
                    setIsLoading(false);
                }
            }
        };

        const getPlayerTeams= async()=>{
            try{
                const response = await fetch('http://localhost:9000/getPlayerTeams', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    credentials: 'include'
                });

                if (response.ok) {
                    const teams = await response.json();
                    setTeams(teams); // Store user data
                } else if (response.status === 401) {
                    throw new Error('Session verification failed');
                } else {
                    throw new Error('An unexpected error occurred');
                }

            }catch (error){
                console.error(error);
            }
        }


        const getPlayerGames= async()=>{
            try{
                const response = await fetch('http://localhost:9000/getPlayerGames', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    credentials: 'include'
                });

                if (response.ok) {
                    const games = await response.json();
                    setGames(games); // Store user data
                } else if (response.status === 401) {
                    throw new Error('Session verification failed');
                } else {
                    throw new Error('An unexpected error occurred');
                }

            }catch (error){
                console.error(error);
            }
        }


        verifyUserSession();
        getPlayerTeams();
    }, [userSession, navigate]);

    if (isLoading) {
        return (
            <div>
                <h1 style={{marginTop:"15%",textAlign:"center",fontSize:"4vw",paddingBottom:"30%"}}>
                    Loading....
                </h1>
            </div>
        );
    } else if (error) {
