import React, {useEffect, useState} from 'react';
import './stylesFiles/createAccount.css';
import InputTextField from './inputTextField';

const CreateAccount = () => {
    const [password, setPassword] = useState('');
    const [retypePassword, setRetypePassword] = useState('');

    const handlePasswordInput = (event) => {
        setPassword(event.target.value);
    }

    const handleRetypePasswordInput = (event) => {
        setRetypePassword(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        if (password !== retypePassword) {
            alert('Passwords do not match!');
            return;
        }
    }


    return (
        <div style={{backgroundImage:"linear-gradient(to top left,black,darkblue)",paddingTop:"5%"}}>
            <h1 style={{fontSize:"4vw"}}>Create Account</h1>
            <form>
                <ul className="createAccountList" style={{listStyleType:"none", marginTop:"5%",paddingRight:"30%", paddingLeft: "30%",paddingBottom:"5%"}}>
                    <li style={{position: "relative"}}>
                        <InputTextField type="text" label="First Name"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="text" label="Last Name"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="email" label="Email Address"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField onChange={handlePasswordInput} type="password" label="Create strong password"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField onChange={handleRetypePasswordInput} type="password" label="Retype password"></InputTextField>
                    </li>
                    <li style={{marginTop:"10%"}}>
                        <label style={{fontSize:"25px", paddingRight:"5%"}} htmlFor="position_selector">Which position do you want to play:</label>
                        <select style={{paddingRight: "10px", paddingLeft:"10px",paddingTop:"5px",paddingBottom:"5px",background:"#edf2ff",border:"none", borderRadius:"20px"}} name="position" id="position_selector">
                            <option value="ATT">Attacker</option>
                            <option value="MID">Midfielder</option>
                            <option value="DEF">Defender</option>
                            <option value="G">Goalkeeper</option>
                        </select>
                    </li>
                    <li style={{marginTop:"10%"}}>
                        <input className="submit_button" type="submit" value="Start your journey"/>
                    </li>
                </ul>
            </form>
        </div>
    );
}

export default CreateAccount;
