import React, {useEffect, useState} from 'react';
import './stylesFiles/createAccount.css';
import InputTextField from './inputTextField';

const CreateAccount = () => {

    const [hasContent, setHasContent] = useState(false);

    const handleBlur = (e) => {
        if (e.target.value !== "") {
            setHasContent(true);
        } else {
            setHasContent(false);
        }
    }

    useEffect(() => {
        if(props.value !== ""){
            setHasContent(true);
        }else{
            setHasContent(false);
        }
    }, [props.value]);

    return (
        <div style={{backgroundImage:"linear-gradient(to top left,black,darkblue)",paddingTop:"5%"}}>
            <h1 style={{fontSize:"4vw"}}>Create Account</h1>
            <form>
                <ul className="createAccountList" style={{listStyleType:"none", marginTop:"5%",paddingRight:"30%", paddingLeft: "30%"}}>
                    <li style={{position: "relative"}}>
                        <InputTextField type="text" label="First Name" className={`textField ${hasContent ? 'has-content' : ''}`}></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="text" label="Last Name"></InputTextField>
                    </li>
                    <li style={{position: "relative"}}>
                        <InputTextField type="email" label="Email Address"></InputTextField>
                    </li>
                    <li>
                        <label htmlFor="cars">Which position do you play:</label>
                        <select name="cars" id="cars">
                            <option value="ATT">Attacker</option>
                            <option value="MID">Midfielder</option>
                            <option value="DEF">Defender</option>
                            <option value="G">Goalkeeper</option>
                        </select>
                    </li>
                </ul>
            </form>

        </div>
    );
}

export default CreateAccount;
