import React from 'react';

const InputTextField = ({ type, label, name, onChange}) => {
    const [hasContent, setHasContent] = useState(false);

    const handleBlur = (e) => {
        if (e.target.value !== "") {
            setHasContent(true);
        } else {
            setHasContent(false);
        }
    }

    return (
        <div>
            <label>{label}</label>
            <input className="textField" type={type} name={name} onChange={onChange} />
        </div>
    );
}

export default InputTextField;
