import React, { useState, useEffect } from 'react';
import { useParams} from 'react-router-dom';
import './stylesFiles/CompetitionInfo.css';


const Game = () => {
    const [gameInfo, setGameInfo] = useState(null);
    const [team1Players, setTeam1Players] = useState([]);
    const [team2Players, setTeam2Players] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    let { gameID } = useParams();


    useEffect(() => {
        const getAllPlayers= async () => {
            try {
                const response = await fetch('http://localhost:9000/getPlayersInGame', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ gameID: gameID }),
                    credentials: 'include',
                });
                if (response.ok) {
                    const data = await response.json();
                    setTeam1Players(data.filter(player => player.TeamID === gameInfo.Team1_ID));
                    setTeam2Players(data.filter(player => player.TeamID === gameInfo.Team2_ID));
                } else {
                    throw new Error('An unexpected error occurred');
                }
            } catch (error) {
                console.error(error);
            }
        };

        const getGameInfo = async () => {
            try {
                const response = await fetch('http://localhost:9000/getGameInfo', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ gameID: gameID }),
                    credentials: 'include',
                });
                if (response.ok) {
                    const data = await response.json();
                    setGameInfo(data);
                } else {
                    throw new Error('An unexpected error occurred');
                }
            } catch (error) {
                console.error(error);
            }
        };
        getGameInfo()
            .then(gameInfo => getAllPlayers(gameInfo.Team1_ID, gameInfo.Team2_ID))  // updated
            .then(() => setLoading(false))
            .catch((error) => setError(error.message));
    }, [gameID]);

    if (loading) {
        return <div>
            <h1 style={{marginTop:"15%",textAlign:"center",fontSize:"4vw",paddingBottom:"30%"}}>
                Loading....
            </h1>
        </div>
    }

    if (error) {
        return <div>
            <h1 style={{marginTop:"15%",textAlign:"center",fontSize:"4vw",paddingBottom:"30%"}}>
                Error: {error}
            </h1>
        </div>
    }

    if(gameInfo && team1Players && team2Players) {
        const game = gameInfo[0];
        return (
            <div>
                <div style={{
                    textAlign: "center", borderRight: "4px solid white", borderLeft: "4px solid white",
                    borderBottom: "4px solid white", marginLeft: "25%", marginRight: "25%"
                }}>
                    <h1>{game.Team1_Name} VS {game.Team2_Name}</h1>
                    <h1>{game.Team1_Goals} - {game.Team2_Goals}</h1>
                    <h1>{game.GameDate}</h1>
                </div>
                <div>
                    <table style={{marginTop: "5%", float: "left", width: "50%", padding: "5px"}}>
                        <tr>
                            <th colSpan="2">
                                {game.Team1_Name}
                            </th>
                            <th>
                                Goals
                            </th>
                            <th>
                                <i className="bi bi-stop-fill" style={{color: "yellow"}}></i>
                            </th>
                            <th>
                                <i className="bi bi-stop-fill" style={{color: "red"}}></i>
                            </th>
                            <th>
                                <i className="bi bi-chevron-double-up"></i>
                            </th>
                        </tr>
                        {team1Players.map((member, index) => (
                            <tr key={index} style={{borderBottom: "1px solid silver"}}>
                                <td>{member.Position}</td>
                                <td>{member.First_Name + " " + member.Last_Name}</td>
                                <td>{member.Player_score}</td>
                                <td>{member.Player_yellow_card}</td>
                                <td>{member.Player_red_card}</td>
                                <td>{member.Player_value_gain}</td>
                            </tr>
                        ))}
                    </table>

                    <table style={{marginTop: "5%", float: "left", width: "50%", padding: "5px"}}>>
                        <tr>
                            <th>
                                <i className="bi bi-chevron-double-up"></i>
                            </th>
                            <th>
                                <i className="bi bi-stop-fill" style={{color: "red"}}></i>
                            </th>
                            <th>
                                <i className="bi bi-stop-fill" style={{color: "yellow"}}></i>
                            </th>
                            <th>
                                Goals
                            </th>
                            <th colSpan="2" className="Game_team_title_2">
                                {game.Team2_Name}
                            </th>
                        </tr>
                        {team2Players.map((member, index) => (
                            <tr key={index} style={{borderBottom: "1px solid silver"}}>
                                <td>{member.Player_score}</td>
                                <td>{member.Player_yellow_card}</td>
                                <td>{member.Player_red_card}</td>
                                <td>{member.Player_value_gain}</td>
                                <td>{member.First_Name + " " + member.Last_Name}</td>
                                <td>{member.Position}</td>
                            </tr>
                        ))}
                    </table>
                </div>
            </div>
        );
    }
};

export default Game;
